import React from "react";
import {Button, Card, Col, Form} from "react-bootstrap";
import axios from "axios";
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faSave, faUndo} from '@fortawesome/free-solid-svg-icons'

class AddRoom extends React.Component {

    constructor(props) {
        super(props);
        this.state = this.initialState

        this.valueChange = this.valueChange.bind(this)
        this.submitRoom = this.submitRoom.bind(this)
    }

    initialState = {
        roomNumber:'',
        pricePerNight:'',
        maxAmountOfBeds: '',
        type: 'SMALL_ROOM',
        description:''
    }

    submitRoom = (event) =>{


        const params = new URLSearchParams();
        params.append('roomNumber',this.state.roomNumber);
        params.append('pricePerNight',this.state.pricePerNight);
        params.append('maxAmountOfBeds',this.state.maxAmountOfBeds);
        params.append('type',this.state.type);
        params.append('description',this.state.description);


        axios({
            method:'post',
            url:'http://localhost:8080/postRoom',
            data: params
        })
        event.preventDefault();
    }

    resetRoom = () => {
        this.setState(() => this.initialState)
    }

    valueChange = (event) =>{
        this.setState({
            [event.target.name]:event.target.value
        })
    }

    render() {
        return(
            <Card className="border border-dark bg-dark text-white">
                <Card.Header>Dodaj pokój</Card.Header>
                <Form onReset={this.roomReset} onSubmit={this.submitRoom} id={"registerFormId"}>
                    <Card.Body>

                        <Form.Row>
                            <Form.Group as={Col} controlId="formBasicRoomNumber">
                                <Form.Label>Numer Pokoju</Form.Label>
                                <Form.Control
                                    required
                                    type="text"
                                    name={"roomNumber"}
                                    value={this.state.roomNumber}
                                    onChange={this.valueChange}
                                    placeholder="A001"
                                    className={"bg-dark text-white"}

                                />

                            </Form.Group>
                            <Form.Group as={Col} controlId="formBasicPricePerNight">
                                <Form.Label>Cena za noc</Form.Label>
                                <Form.Control
                                    required
                                    type="number"
                                    name={"pricePerNight"}
                                    value={this.state.pricePerNight}
                                    onChange={this.valueChange}
                                    placeholder="100.00"
                                    step="0.01"
                                    className={"bg-dark text-white"}
                                />
                            </Form.Group>

                        </Form.Row>

                        <Form.Row>
                            <Form.Group as={Col} controlId="formBasicMaxAmountOfBeds">
                                <Form.Label>Makszymalna liczba łurzek</Form.Label>
                                <Form.Control
                                    required
                                    type="number"
                                    name={"maxAmountOfBeds"}
                                    value={this.state.maxAmountOfBeds}
                                    onChange={this.valueChange}
                                    placeholder="4"
                                    className={"bg-dark text-white"}

                                />
                            </Form.Group>
                            <Form.Group as={Col} controlId="formBasicSurname">
                                <Form.Label>Typ pokoju</Form.Label>
                                <Form.Control
                                    required
                                    as={"select"}
                                    name={"type"}
                                    value={this.state.type}
                                    onChange={this.valueChange}
                                    className={"bg-dark text-white"}
                                >
                                    <option>SMALL_ROOM</option>
                                    <option>MEDIUM_ROOM</option>
                                    <option>BIG_ROOM</option>
                                    <option>APARTAMENT</option>
                                    <option>HOUSE</option>
                                </Form.Control>
                            </Form.Group>

                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formBasicTelephone">
                                <Form.Label>Opis pokoju</Form.Label>
                                <Form.Control
                                    required
                                    type="description"
                                    name={""}
                                    value={this.state.description}
                                    onChange={this.valueChange}
                                    placeholder="Opis"
                                    className={"bg-dark text-white"}

                                />

                            </Form.Group>


                        </Form.Row>

                    </Card.Body>
                    <Card.Footer style={{"textAlign":"right"}}>
                        <Button size="sm" variant="success" type="submit">
                            <FontAwesomeIcon icon={faSave}/> Dodaj
                        </Button>{' '}
                        <Button size="sm" variant="info" type="reset">
                            <FontAwesomeIcon icon={faUndo} /> Resetuj
                        </Button>
                    </Card.Footer>
                </Form>
            </Card>
        );
    }

}

export default AddRoom;
